<?php\n// Database connection setup\n$dsn = 'mysql:host=localhost;dbname=testdb;charset=utf8mb4';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Get form data and prepare for database\n    $formdesc = $_POST['formdesc'];\n\n    // Prepare the statement\n    $stmt = $pdo->prepare("INSERT INTO descriptions (description) VALUES (:description)");\n    \n    // Bind parameters safely\n    $stmt->bindParam(':description', $formdesc);\n    \n    // Execute the statement\n    $stmt->execute();\n\n} catch(PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n// Output to HTML, ensuring proper escaping\necho htmlspecialchars($formdesc, ENT_QUOTES, 'UTF-8');\n?>