<?php\nfunction tailFile($filename) {\n    // Open the file in read-only mode\n    $handle = fopen($filename, "r");\n    \n    if ($handle === false) {\n        echo "Error opening file: $filename\n";\n        return;\n    }\n    \n    // Move to the end of the file\n    fseek($handle, 0, SEEK_END);\n    \n    while (true) { // Infinite loop\n        $whereWeAre = ftell($handle); // Get current position in stream\n        \n        // Check if file has changed since we opened it\n        while ($line = fgets($handle, 4096)) {\n            echo $line;\n            ob_flush();\n            flush(); // Send output to the browser immediately\n        }\n        \n        sleep(1); // Wait for one second before checking again\n        \n        // If the file has not changed, seek to our last position\n        if (ftell($handle) <= $whereWeAre) {\n            fseek($handle, $whereWeAre);\n        }\n    }\n    \n    fclose($handle);\n}\n\n// Example usage\ntailFile('yourfile.log');\n?>