$URL = 'http://www.nanonull.com/TimeService/TimeService.asmx';\n\n$params = new stdClass();\n$params->timezone = 'ZULU';\n\n$client = new SoapClient(null, array(\n    'location' => $URL,\n    'uri'      => "http://www.Nanonull.com/TimeService/",\n    'trace'    => 1,\n));\n\ntry {\n    $return = $client->__soapCall("getTimeZoneTime", array($params), \n        array('soapaction' => 'http://www.Nanonull.com/TimeService/getTimeZoneTime'));\n    \n    print_r($return);\n} catch (SoapFault $e) {\n    echo $client->__getLastRequest();\n    echo $client->__getLastResponse();\n    echo "SOAP Fault: ({$e->faultcode}) {$e->getMessage()}";\n}