$first = 2;\n$second = 5;\n$operation = '*';\n$answer = null;\n\nswitch ($operation) {\n    case '+':\n        $answer = $first + $second;\n        break;\n    case '-':\n        $answer = $first - $second;\n        break;\n    case '*':\n        $answer = $first * $second;\n        break;\n    case '/':\n        if ($second != 0) { // avoid division by zero\n            $answer = $first / $second;\n        } else {\n            throw new Exception("Division by zero");\n        }\n        break;\n    default:\n        throw new Exception("Unsupported operation");\n}\n\necho "The answer is: " . $answer;