<?php\n\n// The URL of the XML file on your server or elsewhere\n$url = "http://your-web-server.com/path/to/your.xml";\n\n// Initialize a CURL session\n$ch = curl_init();\n\n// Set the URL for the request\ncurl_setopt($ch, CURLOPT_URL, $url);\n\n// Return transfer as string\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute the request\n$xmlContent = curl_exec($ch);\n\n// Close the CURL session\ncurl_close($ch);\n\n// Load XML content into a SimpleXML object\n$xmlObject = simplexml_load_string($xmlContent);\n\n// Convert SimpleXML object to an array\n$jsonArray = json_decode(json_encode((array) $xmlObject), true);\n\n// Encode array to JSON string\n$jsonString = json_encode($jsonArray, JSON_PRETTY_PRINT);\n\n// Output the JSON\necho $jsonString;\n\n?>