<?php\n// Assuming this is called through /rpc.php?method=getResourceById&id=123\nheader('Content-Type: application/json');\n$method = $_REQUEST['method'];\n$params = array_slice($_REQUEST, 1); // Get all other parameters\n\nif (function_exists($method)) {\n    echo json_encode(call_user_func_array($method, $params));\n} else {\n    http_response_code(404);\n    echo json_encode(['error' => 'Method not found']);\n}\n?>