require_once 'path/to/HTMLPurifier.auto.php';\n\n$allowedTags = array('strong', 'em', 'blockquote', 'a');\n$config = HTMLPurifier_Config::createDefault();\n$config->set('Core.AllowedElements', $allowedTags);\n$config->set('Output.FlashCompat', false); // Ensures proper XHTML output\n\n$purifier = new HTMLPurifier($config);\n\nfunction sanitizeComment($comment) {\n    global $purifier;\n    return $purifier->purify($comment);\n}\n\n// Example usage\n$comment = "*strong* _em_ [blockquote] http://www.link.com";\n$comment = str_replace(array('*','_'), array('<strong>','<em>'), $comment); // Simple conversion to HTML tags\n$cleanComment = sanitizeComment($comment);\n\necho htmlspecialchars($cleanComment, ENT_QUOTES, 'UTF-8');