// Add this code in your theme's functions.php file\n\nfunction add_custom_query_var($vars) {\n    $vars[] = "size"; // Add 'size' as a valid query var\n    return $vars;\n}\nadd_filter('query_vars', 'add_custom_query_var');\n\nfunction handle_size_query() {\n    global $wp_query; // Get the global WordPress query object\n\n    if (isset($wp_query->query_vars['size'])) {\n        echo "hello world\n";\n        echo $wp_query->query_vars['size'];\n        echo "\nhello world";\n        exit; // Always remember to exit if you are outputting content directly\n    }\n}\nadd_action('template_redirect', 'handle_size_query');