function getPhpTimezoneFromGmtOffset($offset) {\n    // Define a mapping from GMT offsets to PHP time zones\n    $timezoneMap = [\n        '-12.0' => 'Etc/GMT+12',\n        '-11.0' => 'Pacific/Midway',\n        '-10.0' => 'Pacific/Honolulu',\n        '-9.5'  => 'America/Anchorage', // Approximation\n        '-9.0'  => 'America/Los_Angeles',\n        '-8.0'  => 'America/Denver',\n        '-7.0'  => 'America/Mazatlan',\n        '-6.0'  => 'America/Chicago',\n        '-5.0'  => 'America/New_York',\n        '-4.5'  => 'America/Halifax', // Approximation\n        '-4.0'  => 'America/Buenos_Aires', // Approximation\n        '-3.5'  => 'America/Caracas', // Approximation\n        '-3.0'  => 'America/Sao_Paulo',\n        '-2.0'  => 'Atlantic/South_Georgia',\n        '-1.0'  => 'Atlantic/Azores',\n        '+0.0'  => 'Europe/London',\n        '+1.0'  => 'Europe/Berlin',\n        '+2.0'  => 'Europe/Minsk',\n        '+3.0'  => 'Asia/Riyadh',\n        '+3.5'  => 'Asia/Tehran', // Approximation\n        '+4.0'  => 'Europe/Moscow',\n        '+4.5'  => 'Asia/Kabul', // Approximation\n        '+5.0'  => 'Asia/Tashkent',\n        '+5.5'  => 'Asia/Calcutta',\n        '+6.0'  => 'Asia/Dhaka',\n        '+7.0'  => 'Asia/Bangkok',\n        '+8.0'  => 'Asia/Shanghai',\n        '+9.0'  => 'Asia/Tokyo',\n        '+9.5'  => 'Australia/Adelaide', // Approximation\n        '+10.0' => 'Australia/Melbourne',\n        '+11.0' => 'Pacific/Pohnpei',\n        '+12.0' => 'Pacific/Auckland'\n    ];\n\n    // Return the timezone string if found, otherwise return a default\n    return $timezoneMap[$offset] ?? null;\n}\n\n$offset = '-8.0';\n$phpTimezone = getPhpTimezoneFromGmtOffset($offset);\nif ($phpTimezone) {\n    date_default_timezone_set($phpTimezone);\n} else {\n    echo "No timezone found for offset: $offset";\n}