<?php\n$filePath = '/path/to/your/file.txt';\n$filePermissions = substr(sprintf('%o', fileperms($filePath)), -4); // Get file permissions\n\nif ($filePermissions == '0440') {\n    $handle = @fopen($filePath, "r");\n    if ($handle) {\n        echo fread($handle, filesize($filePath));\n        fclose($handle);\n    } else {\n        echo "Failed to open the file.";\n    }\n} else {\n    echo "File permissions are incorrect.";\n}\n?>