<?php\n\nclass Database\n{\n    private $pdo;\n\n    public function __construct($dsn, $username, $password)\n    {\n        $this->pdo = new PDO($dsn, $username, $password);\n    }\n\n    public function FetchRow($query, $params = [])\n    {\n        $stmt = $this->pdo->prepare($query);\n        $stmt->execute($params);\n        $result = $stmt->fetch(PDO::FETCH_ASSOC);\n\n        if ($result === false) {\n            return null;\n        }\n\n        return new DatabaseStatement($result);\n    }\n}\n\nclass DatabaseStatement\n{\n    private $result;\n\n    public function __construct($result)\n    {\n        $this->result = $result;\n    }\n\n    public function __get($column)\n    {\n        if (array_key_exists($column, $this->result)) {\n            return $this->result[$column];\n        }\n        return null; // or throw an exception\n    }\n\n    public function toArray(): array\n    {\n        return $this->result;\n    }\n}\n\n$db = new Database('mysql:host=localhost;dbname=test', 'user', 'pass');\n$result = $db->FetchRow('SELECT * FROM users WHERE id = 1');\n\nif ($result !== null) {\n    echo $result->username; // Access property\n    echo '<pre>' . print_r($result->toArray(), true) . '</pre>'; // Get the array\n}