class Database {\n    private $conn;\n    public $queryCount = 0;\n\n    public function __construct($host, $username, $password, $dbname) {\n        // Initialize connection here and handle exceptions\n        $this->conn = new mysqli($host, $username, $password, $dbname);\n        if ($this->conn->connect_error) {\n            die("Connection failed: " . $this->conn->connect_error);\n        }\n    }\n\n    public function query($sql) {\n        // Increment the query count\n        $this->queryCount++;\n        \n        // Execute the SQL and handle any exceptions\n        $result = $this->conn->query($sql);\n        if (!$result) {\n            die("Query failed: " . $this->conn->error);\n        }\n        \n        return $result;\n    }\n\n    public function getQueryCount() {\n        return $this->queryCount;\n    }\n}\n\n// Usage\n$db = new Database('localhost', 'user', 'password', 'database');\n\n// Perform some queries\n$db->query("SELECT * FROM table1");\n$db->query("SELECT * FROM table2");\n\necho "Page generated with " . $db->getQueryCount() . " queries.";