require 'vendor/autoload.php';\n\nuse OpenCloud\Rackspace;\nuse GuzzleHttp\Exception\ClientException;\n\n// Rackspace credentials\n$username = 'your_rackspace_username';\n$apiKey = 'your_api_key';\n\n// Establish a connection to the Cloud Files service\n$client = new Rackspace(Rackspace::US_IDENTITY_ENDPOINT, [\n    'username' => $username,\n    'apiKey'   => $apiKey,\n]);\n\n$objectStoreService = $client->objectStoreService(null, 'DFW');\n\ntry {\n    // Assuming $fileTmpPath is the location of your file in temp directory\n    // and $fileName is your media file name\n    $container = $objectStoreService->getContainer('your_container_name');\n    \n    // Upload content to a container\n    $obj = $container->uploadObject(\n        new \GuzzleHttp\Psr7\MultipartStream([\n            [\n                'name'     => '',\n                'contents' => fopen($fileTmpPath, 'r'),\n                'filename' => $fileName,\n            ],\n        ])\n        ['name' => $fileName]\n    );\n    \n    // Remove the file from temp after successful upload\n    unlink($fileTmpPath);\n} catch (ClientException $e) {\n    echo "An error occurred: " . $e->getMessage();\n}