// PHP Example\nclass RestClient {\n    public function fetchData($url) {\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $url);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n        $response = curl_exec($ch);\n        if (curl_errno($ch)) {\n            throw new Exception(curl_error($ch));\n        }\n        curl_close($ch);\n        return json_decode($response, true);\n    }\n}