$file = fopen($filepath, "w");\nif ($file === false) {\n    die("Failed to open file for writing.");\n}\n\n$CR = curl_init();\ncurl_setopt($CR, CURLOPT_URL, $source_path);\ncurl_setopt($CR, CURLOPT_POST, 1);\ncurl_setopt($CR, CURLOPT_FAILONERROR, true);\ncurl_setopt($CR, CURLOPT_RETURNTRANSFER, true); // Capture output\ncurl_setopt($CR, CURLOPT_SSL_VERIFYPEER, true); // Verify SSL certificate\ncurl_setopt($CR, CURLOPT_FILE, $file);\n\n$result = curl_exec($CR);\nif ($result === false) {\n    echo 'Curl error: ' . curl_error($CR);\n} else {\n    print filesize($filepath);\n}\n\n// Clean up\nfclose($file);\ncurl_close($CR);