// src/Application.php\n\nnamespace App;\n\nuse Cake\Core\Application;\nuse Cake\ORM\Locator\TableLocator;\n\nclass Application extends BaseApplication\n{\n    public function bootstrap(): void\n    {\n        // Call parent to load existing bootstrap logic.\n        parent::bootstrap();\n\n        // Custom logic for fetching timezone from database and setting the environment variable.\n        $table = TableLocator::getTable('Settings'); // Adjust table name as necessary\n        if ($table) {\n            $setting = $table->find()->where(['key' => 'timezone'])->first();\n            if ($setting && !empty($setting->value)) {\n                putenv("TZ=" . $setting->value);\n                date_default_timezone_set($setting->value);\n            }\n        }\n    }\n}