<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Initialize an array to hold the results\n    $formData = [];\n\n    // Loop through all POST data\n    foreach ($_POST as $key => $value) {\n        // Here, $key is the input's name/id and $value is its value\n        $formData[$key] = htmlspecialchars($value);\n    }\n\n    // You can now use $formData to process or store the data securely\n    print_r($formData);\n\n    // Example of storing the data in a secure manner (e.g., database)\n    // Remember to validate and sanitize input before saving\n}\n?>