<?php\n\n// application/bootstrap.php or similar initialization script\n\nrequire_once 'Zend/Application/Autoloader/ResourceAutoload.php';\n\n// Register your custom namespace with the autoloader\n$loader = new Zend_Loader_Autoloader_Resource(array(\n    'basePath'  => APPLICATION_PATH . '/library/Core',\n    'namespace' => 'Core',\n));\n\n// If you have other resource types, include them here\n$loader->addResourceType('filter', 'Filter/', 'Filter')\n       ->addResourceType('validator', 'Validator/', 'Validator');\n\n// Use Zend_Filter_Input with your custom filters and validators\n$filters = array(\n    'month'   => 'Digits',\n    'account' => 'StringTrim',\n    'other'   => 'Core_Filter_MyFilter',\n);\n\n$validators = array(\n    'account' => 'Alpha',\n    'other'   => 'Core_Validator_MyValidator',\n);\n\n$inputFilter = new Zend_Filter_Input($filters, $validators);