<?php if ($this->Session->read('Message.flash')): ?>\n<div class="contentBlockWrapper <?php echo $this->Session->check('Message.error') ? 'contentError' : 'contentSuccess'; ?>">\n    <div class="contentBlock">\n        <div id="flashMessage" class="flash">\n            <?php\n                // Manually output the flash message\n                $flashData = $this->Session->read('Message.flash');\n                echo isset($flashData['message']) ? h($flashData['message']) : '';\n                // Clear the flash message from the session after outputting\n                $this->Session->delete('Message.flash');\n            ?>\n        </div>\n    </div>\n</div>\n<?php endif; ?>