// Create the text element\n$name = new Zend_Form_Element_Text('name');\n$name->setLabel('Name');\n\n// Set custom decorators for the element\n$name->setDecorators(array(\n    'ViewHelper',\n    array('HtmlTag', array('tag' => 'dd')),\n    array('Label', array('tag' => 'dt')),\n    // Add a decorator for the link\n    array('HtmlTag', array('tag' => 'dd', 'placement' => 'APPEND', 'openOnly' => true)),\n    array(array('linkDecorator' => 'HtmlTag'), array('tag' => '', 'content' => '<a href="#">My Link</a>')),\n));\n\n// Add the element to the form\n$this->addElements(array($name));