$value = ""; // Your value which could be an empty string\n\n// Prepare the SQL statement with explicit types\n$query = "INSERT INTO mytable (somevarchar) VALUES (?)";\n$types = array('text'); // 'text' type for VARCHAR fields in most cases\n\n// Prepare and execute the statement\ntry {\n    $stmt = $db->prepare($query, $types);\n    $result = $stmt->execute(array($value));\n} catch (Exception $e) {\n    echo "An error occurred: " . $e->getMessage();\n}