// Corrected HTML form\necho '<form method="post">';\necho 'Length: <input name="length" value="10" type="text" id="length" size="2" />';\necho '<input type="submit" value="Submit" />';\necho '</form>';\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Convert the POST variable to an integer\n    $length = intval($_POST['length']);\n    \n    // Example URL for demonstration purposes\n    $url = "http://example.com/this/is/a/test/url";\n\n    // Use the converted integer in substr function\n    $result = substr($url, 0, $length);\n    echo 'Result: ' . htmlspecialchars($result); // Output the result safely\n}