function isFileMimeTypeAllowed($filePath, $allowedTypes) {\n    // Create a finfo resource\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n    \n    // Get the MIME type of the file\n    $mimeType = finfo_file($finfo, $filePath);\n    \n    // Close the finfo resource\n    finfo_close($finfo);\n    \n    // Check if the MIME type is in the allowed list\n    return in_array($mimeType, $allowedTypes);\n}\n\n// Example usage\n$uploadedFile = 'somezipfile.png';\n$allowedMimeTypes = ['image/png', 'application/pdf']; // Add all allowed MIME types\n\nif (isFileMimeTypeAllowed($uploadedFile, $allowedMimeTypes)) {\n    echo "The file type is allowed.";\n} else {\n    echo "This file type is not allowed.";\n}