$logFile = '/path/to/your/logfile.log';\n$requestData = file_get_contents("php://input");\n$expectedSize = $_SERVER['CONTENT_LENGTH'];\n\nif ($requestData !== false) {\n    error_log(\n        "Request Length: {$expectedSize}, Data Size: " . strlen($requestData) . \n        ", Headers: " . print_r(http_get_request_headers(), true), 3, $logFile\n    );\n} else {\n    error_log("Failed to read request body", 3, $logFile);\n}