$result = mysql_query("SELECT role FROM users");\n\nif (@mysql_num_rows($result)) {\n    while ($r = @mysql_fetch_assoc($result)) {  \n        $role = $r["role"];\n        // Re-initialize $role_drop for each user\n        $role_drop = "<select name='role'>";\n\n        // Add options with the selected attribute if the role matches\n        $options = [\n            'Resident Assistant', \n            'Community Assistant', \n            'Head RA'\n        ];\n\n        foreach ($options as $option) {\n            $selected = ($option === $role) ? "selected=\"\"" : "";\n            $role_drop .= "\r\n<option value='$option' $selected>$option</option>";\n        }\n\n        $role_drop .= "\r\n</select>";\n\n        // Echo the dropdown for the current user\n        echo $role_drop;\n    }\n}