// Sample multi-dimensional associative array\n$array = [\n    'key1' => ['subkey1' => 'value1', 'subkey2' => 'value2'],\n    'key2' => [1, 2, 3],\n];\n\n// Encoding as JSON\n$jsonString = json_encode($array);\nfile_put_contents('cache.json', $jsonString);\n\n// Decoding from JSON\n$cachedArray = json_decode(file_get_contents('cache.json'), true);\n\n// Encoding using serialize\n$serializedString = serialize($array);\nfile_put_contents('cache.ser', $serializedString);\n\n// Decoding using unserialize\n$cachedArraySerial = unserialize(file_get_contents('cache.ser'));