<?php\n$keyword = 'example'; // This would be replaced by user input\n$api_key = 'your_secret_api_key';\n$url = "https://yourdomain.com/search_api.php?q=" . urlencode($keyword) . "&api_key=" .urlencode($api_key);\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$response = curl_exec($ch);\ncurl_close($ch);\n\n$jsonData = json_decode($response, true);\n\nif (isset($jsonData['error'])) {\n    echo 'Error: ' . $jsonData['error'];\n} else {\n    foreach ($jsonData['results'] as $result) {\n        // Display your results\n        echo '<p>' . htmlspecialchars($result['column_name']) . '</p>';\n    }\n}\n?>