// Convert binary IPv6 address to DECIMAL(39,0)\nfunction ipv6_bin_to_dec($binary_ipv6) {\n    // Unpack the 128-bit binary into two unsigned longs (each 64 bits)\n    list(,$high, $low) = unpack('N2', $binary_ipv6);\n    // Merge high and low parts to get a string representation\n    return bcadd(bcmul($high, '340282366920938463463374607431768211456'), $low);\n}\n\n// Convert DECIMAL(39,0) to binary IPv6 address\nfunction ipv6_dec_to_bin($decimal_ipv6) {\n    // Calculate high and low parts from the decimal value\n    $high = bcdiv($decimal_ipv6, '340282366920938463463374607431768211456');\n    $low  = bcmod($decimal_ipv6, '340282366920938463463374607431768211456');\n    // Pack the high and low values into a binary IPv6 address\n    return pack('NN', $high, $low);\n}\n\n// Example usage:\n$binary_ipv6 = inet_pton("2001:db8::ff00:42:8329");\n$decimal_ipv6 = ipv6_bin_to_dec($binary_ipv6);\necho "Decimal representation: " . $decimal_ipv6 . "\n";\n\n$reversed_binary_ipv6 = ipv6_dec_to_bin($decimal_ipv6);\n$human_readable_ipv6 = inet_ntop($reversed_binary_ipv6);\necho "Human-readable IPv6: " . $human_readable_ipv6 . "\n";