<?php\nfunction timeStringToSeconds($time) {\n    $parts = explode(':', $time);\n    return ($parts[0] * 3600) + ($parts[1] * 60) + (int)$parts[2];\n}\n\n$t1 = timeStringToSeconds("3:15:00");\n$t2 = timeStringToSeconds("1:00:00");\n$t3 = timeStringToSeconds("2:00:00");\n$t4 = timeStringToSeconds("9:00:00");\n\necho $t1 . "\n";\necho $t2 . "\n";\necho $t3 . "\n";\necho $t4 . "\n";\n?>