<?php\n$connection = new mysqli("host", "user", "pass", "sessions");\n\n// Assume $session_id is fetched from Cookies or GET request\n$session_id = $_COOKIE['CGISESSID'] ?? '';\n\n$query = "SELECT a_session FROM sessions WHERE id = ?";\n$stmt = $connection->prepare($query);\n$stmt->bind_param('s', $session_id);\n$stmt->execute();\n$result = $stmt->get_result();\n\nif ($row = $result->fetch_assoc()) {\n    $session_data = json_decode($row['a_session'], true); // Convert JSON to PHP array\n}\n\n// Now you can use the session data in your PHP code\n?>