public function __construct()\n{\n    try {\n        $config = Zend_Registry::get('config');\n        \n        if (!$config instanceof Zend_Config) {\n            throw new Exception("Configuration is not set in Zend_Registry.");\n        }\n\n        require_once('Smarty/Smarty.class.php');\n\n        $this->_engine = new Smarty();\n        $this->_engine->template_dir = $config->paths->templates;\n        $this->_engine->compile_dir = sprintf('%s/tmp/templates_c', $config->paths->data);\n\n        if (!file_exists($this->_engine->compile_dir)) {\n            mkdir($this->_engine->compile_dir, 0755, true);\n        }\n\n        $this->_engine->plugins_dir = array(\n            $config->paths->base . '/include/Templater/plugins',\n            'plugins'\n        );\n\n    } catch (Exception $e) {\n        error_log("Error initializing Templater: " . $e->getMessage());\n        throw $e;\n    }\n}