function generateUrlSlug($str) {\n    // Convert the string to lowercase\n    $str = strtolower($str);\n    \n    // Replace spaces with hyphens (-)\n    $str = str_replace(' ', '-', $str);\n\n    // Remove any characters that are not alphanumeric or hyphens\n    $str = preg_replace('/[^a-z0-9\-]/', '', $str);\n    \n    // Repeatedly replace double hyphens until none left\n    while (strpos($str, '--') !== false) {\n        $str = str_replace('--', '-', $str);\n    }\n\n    return trim($str, '-');\n}\n\n// Example usage:\n$questionName = "Is it better to use ob_get_contents() or \$text .= 'test';";\n$urlSlug = generateUrlSlug($questionName);\necho $urlSlug; // Outputs: is-it-better-to-use-ob-get-contents-or-text-test