class view {\n    public static function getUserSelector($options = []) {\n        $onSelectJs = isset($options['onSelect']) ? $options['onSelect'] : 'function() {}';\n        \n        return <<<HTML\n<div id="user_selector" onSelect="$onSelectJs">\n    <div class="user">\n        <h1>Username1</h1>\n        <a href="javascript:selectUser(this);">select</a>\n    </div>\n    <div class="user">\n        <h1>Username2</h1>\n        <a href="javascript:selectUser(this);">select</a>\n    </div>\n</div>\nHTML;\n    }\n}