<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\IOFactory;\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\n\n// Create new Spreadsheet object\n$spreadsheet = new Spreadsheet();\n\n// Add some data to Sheet1\n$sheet1 = $spreadsheet->getActiveSheet();\n$sheet1->setTitle("Sheet1");\n$sheet1->setCellValue('A1', 'Hello World !');\n\n// Add a second sheet\n$spreadsheet->createSheet();\n$sheet2 = $spreadsheet->getSheet(1);\n$sheet2->setTitle("Sheet2");\n$sheet2->setCellValue('B2', 'Data in Sheet 2');\n\n// Add a third sheet\n$spreadsheet->createSheet();\n$sheet3 = $spreadsheet->getSheet(2);\n$sheet3->setTitle("Sheet3");\n$sheet3->setCellValue('C3', 'Info from Sheet 3');\n\n// Set the active sheet index to the first sheet,\n// so Excel opens this as the first sheet\n$spreadsheet->setActiveSheetIndex(0);\n\n// Redirect output to a clientâ€™s web browser (Excel2007)\nheader('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\nheader('Content-Disposition: attachment;filename="MyReport.xlsx"');\nheader('Cache-Control: max-age=0');\n\n$writer = IOFactory::createWriter($spreadsheet, 'Xlsx');\n$writer->save('php://output');\n\nexit;