function getBody($pid)\n   {\n       $qry = 'SELECT * FROM cart_product WHERE product_id = '.(int)$pid;\n       $result = mysql_query($qry) or die(mysql_error());\n       if ($row = mysql_fetch_assoc($result)) {\n\n           $item_name = htmlspecialchars($row['product_name']);\n           $item_url = htmlspecialchars('product.php?pid='.$pid);\n           $item_image_url = htmlspecialchars('product_images/'.$pid.'_sm_'.$row['product_image_sm']);\n\n           $html = '<div style="width:600px; font-family:Arial, Helvetica, sans-serif; color:#333333;">'\n                   . '...'\n                   . '</div>';\n\n           return $html;\n       }\n       return '';\n   }