function formatData($rawData) {\n    $formattedData = [];\n    foreach ($rawData as $data) {\n        // Example transformations\n        $data['formatted_date'] = date('Y-m-d H:i:s', strtotime($data['unformatted_date']));\n        $data['calculated_value'] = $data['foo'] * $data['bar'];\n        $data['formatted_field'] = substr($data['long_field'], 0, 100); // Simplified example\n\n        array_push($formattedData, $data);\n    }\n    return $formattedData;\n}\n\n// Example usage\n$rawQueryResults = fetchFromDatabase();\n$formattedResults = formatData($rawQueryResults);