$index = isset($_GET['index']) ? (int)$_GET['index'] : 0;\n$Keys = array('column1', 'column2'); // Replace with actual column names\n\n$link = mysqli_connect($Host, $User, $Password, $Database);\nif (!$link) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n$query = "SELECT * FROM transactions WHERE id > ? ORDER BY id";\n$stmt = mysqli_prepare($link, $query);\nmysqli_stmt_bind_param($stmt, 'i', $index);\nmysqli_stmt_execute($stmt);\n\n$result = mysqli_stmt_get_result($stmt);\n$plist = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!DOCTYPE plist PUBLIC \"-//Apple//DTD PLIST 1.0//EN\" \"http://www.apple.com/DTDs/PropertyList-1.0.dtd\">\n<plist version=\"1.0\">\n<array>";\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $plist .= "\t<dict>\n";\n\n    foreach ($Keys as $key) {\n        $plist .= "\t\t<key>$key</key>\n";\n        $plist .= "\t\t<string>" . htmlspecialchars($row[$key]) . "</string>\n"; // Prevent XML injection\n    }\n\n    $plist .= "\t</dict>\n";\n}\n\n$plist .= "</array>\n</plist>";\n\necho $plist;\n\nmysqli_close($link);