<?php\n// Assuming these are your variables from the form POST request\n$clientName = isset($_POST['clientName']) ? $_POST['clientName'] : '';\n$cityId = isset($_POST['cityId']) ? intval($_POST['cityId']) : 0; // Assume city ID is an integer\n$mainBusiness = isset($_POST['mainBusiness']) ? $_POST['mainBusiness'] : '';\n\n$sql = "SELECT c.* \n        FROM Clients c \n        LEFT JOIN Cities ci ON c.city_id = ci.id \n        WHERE 1"; // Start with a base condition that doesn't filter anything\n\nif (!empty($clientName)) {\n    $sql .= " AND c.name LIKE ?";\n    $params[] = "%$clientName%";\n}\n\nif ($cityId > 0) {\n    $sql .= " AND c.city_id = ?";\n    $params[] = $cityId;\n}\n\nif (!empty($mainBusiness)) {\n    $sql .= " AND c.main_business LIKE ?";\n    $params[] = "%$mainBusiness%";\n}\n\n// Assuming you are using PDO for database interaction\n$stmt = $pdo->prepare($sql);\n$stmt->execute($params ?? []);\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($results as $row) {\n    // Process each result row here...\n}\n?>