use Cake\ORM\TableRegistry;\n\nclass MyModel extends AppModel {\n    public function getDataFromMultipleTables() {\n        $table1 = TableRegistry::getTableLocator()->get('TableOne');\n        $table2 = TableRegistry::getTableLocator()->get('TableTwo');\n\n        // Example queries\n        $dataFromTableOne = $table1->find('all');\n        $dataFromTableTwo = $table2->find('all');\n\n        return [\n            'data_from_table_one' => $dataFromTableOne,\n            'data_from_table_two' => $dataFromTableTwo\n        ];\n    }\n}