<?php\n// Assuming $mysqli is your MySQLi connection object\n\n$parent = '1';\n$query = "\nSELECT t1.*\nFROM Table1 t1\nLEFT JOIN Table2 t2 ON t1.ID = t2.accountID AND t2.parent = ?\nWHERE t2.accountID IS NULL;\n\n";\n\n$stmt = $mysqli->prepare($query);\nif ($stmt) {\n    $stmt->bind_param("s", $parent);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    while ($row = $result->fetch_assoc()) {\n        echo $row['ID'] . ", " . $row['Name'] . ", " . $row['Country'] . "<br>";\n    }\n\n    $stmt->close();\n} else {\n    echo "Error preparing statement: " . $mysqli->error;\n}\n?>