<?php\n// Assume $articleType holds the type of article retrieved from the database\n$articleType = 'News'; // This would come from your database\n\n$options = ['News', 'Gossip', 'Travel', 'Performances', 'Others'];\n\necho '<select name="type_of_article">';\nforeach ($options as $option) {\n    $selected = ($option == $articleType) ? 'selected' : '';\n    echo "<option value=\"$option\" $selected>$option</option>";\n}\necho '</select>';\n?>