$router = self::$frontController->getRouter();\n\n// Common defaults and constraints for routes\n$defaults = array("controller" => "index", "action" => "index");\n$constraints = array("id" => "\d+");\n\n// Define shortcutOne\n$route1 = new Zend_Controller_Router_Route(":module/:id", $defaults, $constraints);\n$router->addRoute('shortcutOne', $route1);\n\n// Define shortcutTwo with partially overridden defaults\n$route2Defaults = array_merge($defaults, array("action" => "index"));\n$route2 = new Zend_Controller_Router_Route(":module/:controller/:id", $route2Defaults, $constraints);\n$router->addRoute('shortcutTwo', $route2);\n\n// Define shortcutThree with no defaults as specified\n$route3 = new Zend_Controller_Router_Route(":module/:controller/:action/:id", null, $constraints);\n$router->addRoute('shortcutThree', $route3);