// Model\nclass UserModel {\n    private $name;\n    \n    public function setName($name) {\n        $this->name = $name;\n    }\n    \n    public function getName() {\n        return $this->name;\n    }\n}\n\n// View\nclass UserView {\n    public function printUser(UserModel $model) {\n        echo "Name: ",$model->getName(), "";\n    }\n}\n\n// Controller\nclass UserController {\n    private $model;\n    private $view;\n\n    public function __construct(UserModel $model, UserView $view) {\n        $this->model = $model;\n        $this->view = $view;\n    }\n\n    public function setName($name) {\n        $this->model->setName($name);\n    }\n\n    public function updateView() {\n        $this->view->printUser($this->model);\n    }\n}