<?php\n// Assuming $conn is your PDO connection object\n\n// Get today's date in yyyy-mm-dd format\n$today = date('Y-m-d');\n\n// SQL query to find overlapping date ranges with today's date\n$sql = "SELECT * FROM time_off_requests \n        WHERE :today BETWEEN date_from AND date_to";\n\n// Prepare and execute the statement\n$stmt = $conn->prepare($sql);\n$stmt->bindParam(':today', $today, PDO::PARAM_STR);\n$stmt->execute();\n\n// Fetch results\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nif (!empty($results)) {\n    echo "There are requests for today.";\n    // Process the results as needed\n} else {\n    echo "No requests for today.";\n}\n?>