<?php\n\n// URL of the remote text file\n$remoteFileUrl = 'http://example.com/products.txt';\n\n// Fetch the content of the remote file\n$fileContent = @file_get_contents($remoteFileUrl);\n\nif ($fileContent === FALSE) {\n    die('Error reading the file.');\n}\n\n// Split the file into lines\n$lines = explode("\n", $fileContent);\n\n// Initialize an array to hold product data\n$products = [];\n\n// Process each line\nforeach ($lines as $line) {\n    // Trim whitespace and split by tabs\n    $productData = explode("\t", trim($line));\n\n    if (count($productData) >= 3) { // Ensure we have enough elements\n        $product['number'] = trim($productData[0]);\n        $product['name'] = trim($productData[1]); // Join all remaining parts as name\n        $product['price'] = trim(end($productData));\n\n        // Remove the price from the name string\n        $product['name'] = str_replace($product['price'], '', $product['name']);\n        $product['name'] = trim($product['name']); // Trim again\n\n        // Add product data to array\n        $products[] = $product;\n    }\n}\n\n// Print out products for demonstration purposes\nforeach ($products as $product) {\n    echo "Number: {$product['number']} | Name: {$product['name']} | Price: {$product['price']}\n";\n}\n\n?>