function exportCSV($table) {\n    // Use prepared statements to prevent SQL injection\n    global $conn; // Assuming you have a database connection stored in $conn\n\n    // Fetch column names\n    $result = mysqli_query($conn, "SHOW COLUMNS FROM `" . $table . "`");\n    if ($result === false) {\n        die('Error fetching columns: ' . mysqli_error($conn));\n    }\n\n    $csv_output = '';\n    $i = 0;\n    if (mysqli_num_rows($result) > 0) {\n        while ($row = mysqli_fetch_assoc($result)) {\n            $csv_output .= '"' . addslashes($row['Field']) . '";';\n            $i++;\n        }\n    }\n    $csv_output .= "\n";\n\n    // Fetch table data\n    $values = mysqli_query($conn, "SELECT * FROM `" . $table . "`");\n    if ($values === false) {\n        die('Error fetching data: ' . mysqli_error($conn));\n    }\n\n    while ($rowr = mysqli_fetch_row($values)) {\n        for ($j=0;$j<$i;$j++) {\n            // Escape special characters\n            $csv_output .= '"' . addslashes($rowr[$j]) . '";';\n        }\n        $csv_output .= "\n";\n    }\n\n    // Define filename with unique timestamp\n    $filename = basename($table) . "_" . date("Y-m-d_H-i", time());\n\n    // Send headers to trigger download\n    header("Content-type: text/csv");\n    header("Content-disposition: attachment; filename=" . $filename . ".csv");\n\n    // Output the CSV content\n    echo $csv_output;\n    exit;\n}