function page_link_by_title($title) {\n    // Get the global wgScriptPath from MediaWiki configuration\n    global $wgScriptPath;\n\n    // Use Title::newFromText to get a Title object\n    $articleTitle = \MediaWiki\Title\Title::newFromText($title);\n\n    // If the title is valid, get the full URL\n    if ($articleTitle) {\n        return htmlspecialchars($articleTitle->getFullUrl());\n    } else {\n        // Handle invalid title gracefully\n        return '';\n    }\n}