function send_email_on_new_comment($comment_ID) {\n    $comment = get_comment($comment_ID);\n    if (!empty($_POST['friend_name']) && !empty($_POST['friend_email'])) {\n        $friend_name = sanitize_text_field($_POST['friend_name']);\n        $friend_email = sanitize_text_field($_POST['friend_email']);\n\n        $headers[] = 'Content-Type: text/html; charset=UTF-8';\n\n        $message = '<p>' . __('Hey') . ' ' . esc_html($friend_name) . ',</p>';\n        $message .= '<p>' . esc_html($comment->comment_author) . ' ' . __('said something about you. Isn\'t that cool?') . '</p>';\n        $message .= '<p>' . apply_filters('the_content', convert_chars(wptexturize($comment->comment_content))) . '</p>';\n\n        wp_mail($friend_email, 'A comment was made for you!', $message, $headers);\n    }\n}\nadd_action('comment_post', 'send_email_on_new_comment');