function testParam($fruit, $options = []) {\n    // Define defaults and merge with provided options\n    $defaults = [\n        'veg' => 'pota',\n        'test' => 'default test'\n    ];\n    \n    $options = array_merge($defaults, $options);\n    \n    echo '<br>$fruit = '.$fruit;\n    echo '<br>$veg = '.$options['veg'];\n    echo '<br>Test = '.$options['test'];\n}\n\n// Example calls\necho 'with all params';\ntestParam('apple', ['veg' => 'carrot', 'test' => 'some string']);\n\necho '<hr> missing veg';\ntestParam('apple', ['test' => 'something']);\n\necho '<hr> with default veg, specific test';\ntestParam('apple', ['test' => 'i am set']);