$query = "SELECT comment, userid, UNIX_TIMESTAMP(dtime)\n          FROM comment_updates\n          WHERE updateid=46546\n          ORDER BY dtime DESC\n          LIMIT 10";\n$results = []; // Array to hold all the results\n\nif ($stmt = $this->conn->prepare($query)) {\n    $stmt->execute();\n    \n    // Use reflection to call bind_result with an arbitrary number of variables\n    $meta = $stmt->result_metadata();\n    while ($field = $meta->fetch_field()) {\n        $params[] = &$row[$field->name]; \n    }\n    call_user_func_array(array($stmt, 'bind_result'), $params);\n    \n    // Fetch the results into an associative array\n    while ($stmt->fetch()) {\n        $copy = array();\n        foreach ($row as $key => $val) {\n            $copy[$key] = $val;\n        }\n        $results[] = $copy; // Store each row in the results array\n    }\n\n    $stmt->close();\n}\n\n// Now $results contains all rows fetched from the database\nreturn $results;